// -----------------------------------------------------------------------------
// This file contains styles that are specific to the home page.
// --
body {
    background: $bg-color;
    color: $primary-color;
    text-align: center;
    font-family: $font-secondary;
}

.c-wrapp {
    height: 100%;
    @include breakpoint(desktop) {
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
    }
}

.c-main {
    margin: 0 auto;
    /*max-width: 1140px;*/
    width: 100%;
    @include breakpoint(desktop) {
        @include flexbox();
        @include align-items(center);
        @include justify-content(center);
    }
}

.c-section {
    height: 506px;
    @include breakpoint(desktop) {
        height: 672px;
        width: 384px;
    }
    &__trip {
        height: 460px;
        @include breakpoint(desktop) {
            height: 672px;
            width: 328px;
        }
    }
    &__moonwalker {
        height: 280px;
        @include breakpoint(desktop) {
            height: 672px;
            width: 700px;
        }
    }
}

.c-article {
    @include flexbox();
    @include flex-direction(column);
    background-repeat: no-repeat;
    height: 100%;
    &__visa {
        background-image: url(./../images/rock.jpg), url(./../images/rocket.png);
        background-size: 100% 150px, auto 100%;
        background-position: top center, 35% 150px;
        padding: 6.5rem 4rem 4rem;
        @include breakpoint(desktop) {
            background-image: url(./../images/rock.jpg);
            background-size: cover;
            background-position: right top;
            position: relative;
        }
    }
    &__trip {
        @include align-items(flex-start);
        @include justify-content(flex-end);
        background-image: url(./../images/background.jpg);
        background-position: 25% center;
        background-size: auto 230%;
        padding: 0 2rem;
        position: relative;
        text-align: left;
        z-index: 1;
        &:before {
            content: '';
            background: rgba(0, 0, 0, 0.1);
            left: 0;
            height: 100%;
            position: absolute;
            top: 0;
            width: 100%;
            z-index: 1;
        }
        @include breakpoint(desktop) {
            &:after {
                content: '';
                background-image: url(./../images/background.jpg);
                background-size: 300% auto;
                background-position: left -5rem;
                background-repeat: no-repeat;
                height: 480px;
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
            }
            @include justify-content(flex-start);
            background-image: url(./../images/background.jpg);
            background-size: auto 106%;
            background-position: left bottom;
        }
    }
    &__moonwalker {
        background-image: url(./../images/background.jpg);
        background-position: -210px bottom;
        background-size: 400%;
        @include breakpoint(desktop) {
            @include flexbox();
            @include flex-direction(column);
            @include justify-content(flex-end);
            @include align-items(flex-start);
            background-position: right bottom;
            background-size: 150% auto;
            position: relative;
            z-index: 1;
            width: 700px;
            &:before,
            &:after {
                background-repeat: no-repeat;
                position: absolute;
                top: 0;
            }
            &:before {
                content: '';
                background-image: url(./../images/background.jpg);
                background-position: center -5rem;
                background-size: auto 150%;
                height: 480px;
                left: 0;
                width: 330px;
            }
            // &:after {
            //     content: '';
            //     background-image: url(./../images/rocket.png);
            //     background-position: center top;
            //     background-size: auto 480px;
            //     height: 100%;
            //     left: 330px;
            //     width: calc(100% - 330px);
            // }
        }
    }
}

.c-h1 {
    @include font-size(4);
    margin-top: auto;
    font-family: $font-primary;
    font-weight: 700;
    line-height: 90%;
    text-align: left;
    @include breakpoint(desktop) {
        @include font-size(8);
        left: 28rem;
        position: absolute;
        top: 48%;
        white-space: nowrap;
        z-index: 2;
    }
}

.c-h2 {
    @include font-size(2.5);
    line-height: 168%;
    font-weight: 600
}

.c-logo {
    background-image: url('./../images/logo.png');
    background-size: cover;
    background-repeat: no-repeat;
    height: 140px;
    width: 140px;
    margin: 0 auto;
    @include breakpoint(tablet) {
        height: 200px;
        width: 200px;
        margin-left: 10rem;
    }
}

.c-icon__rocket {
    position: relative;
    z-index: 2;
    &:before,
    &:after {
        content: '';
        left: -3px;
        position: absolute;
        width: 99px;
    }
    &:before {
        background-image: url('./../images/icon_rocket.png');
        height: 97px;
        top: -95px;
    }
    &:after {
        background: $secondary-color;
        bottom: -3px;
        height: 3px;
        width: 94px;
    }
    @include breakpoint(desktop) {
        margin-top: 14rem;
    }
}

.c-tags__list {
    @include flexbox();
    @include flex-wrap(wrap);
    margin-top: 1.8rem;
    margin-bottom: 9.5rem;
    z-index: 2;
}

.c-tag__item {
    @include font-size(2);
    font-style: italic;
    margin-right: .5ch;
}

.c-slider-controls {
    @include breakpoint(desktop) {
        @include flexbox();
        @include flex-direction(column);
        @include align-items(flex-start);
        background: $secondary-bg;
        height: 260px;
        padding: 7rem 5rem 2.4rem;
        margin-bottom: -6.8rem;
        width: 330px;
    }
}

.c-loading {
    background: $primary-color;
    height: 3px;
    position: relative;
    margin: 0 auto;
    width: 90%;
}

.c-bar {
    background: $secondary-color;
    height: 3px;
    left: 0;
    position: absolute;
    top: 0;
    width: 30%;
}

.c-nav {
    margin-top: auto;
    width: 100%;
}

.c-nav__list {
    @include flexbox();
    @include justify-content(space-between);
}

.c-nav__item {
    @include font-size(1.8);
}

.c-nav__link {
    color: $primary-color;
    display: block;
    line-height: 3rem;
    position: relative;
    &:hover {
        color: $secondary-color;
    }
}

.c-nav__link-ease {
    &:after {
        content: '';
        position: absolute;
        bottom: -3px;
        height: 3px;
        left: 0;
        width: 100%;
        background: $primary-color;
    }
    &:hover {
        &:after {
            background: $secondary-color;
        }
    }
}

.c-slider {
    @include breakpoint(desktop) {
        height: 480px;
        left: 0;
        overflow: hidden;
        position: absolute;
        top: 0;
        width: 100%;
    }
}

.c-img__wrapper {
    height: 100%;
    width: 100%;
}

.c-img {
    height: 100%;
    width: 100%;
    object-fit: cover;
}

.slide-entry {
    height: 100%;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    margin: 0 auto;
    text-align: center;
    z-index: 20;
    opacity: 0;
    visibility: hidden;
    transition: 300ms;
    &.active {
        opacity: 1;
        visibility: visible;
    }
}
